import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import Homepage from './Homepage';
import Profile from './Profile';
import Posts from './Posts';
import Messages from './Messages';
import Register from './Register';
import Login from './Login';
import './theme.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import SpaceBackground from "./SpaceBackground";

function Navbar({ user, setUser }) {
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm pastel-navbar">
            <div className="container">
                <Link className="navbar-brand d-flex align-items-center" to="/">
                    <span style={{ fontWeight: 700, color: '#7c7f9a' }}>Swirl</span>
                </Link>
                <div className="collapse navbar-collapse">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
                        {!user && <li className="nav-item"><Link className="nav-link" to="/login">Login</Link></li>}
                        {!user && <li className="nav-item"><Link className="nav-link" to="/register">Register</Link></li>}
                        {user && (
                            <>
                                <li className="nav-item"><Link className="nav-link" to="/profile">Profile</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/posts">Posts</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/messages">Messages</Link></li>
                                <li className="nav-item"><button className="btn btn-link nav-link" onClick={() => setUser(null)}>Logout</button></li>
                            </>
                        )}
                    </ul>
                </div>
            </div>
        </nav>
    );
}

function HomeWithAuth({ user, setUser, onLogin, onRegisterComplete }) {
    // Show homepage, login, and register on the homepage
    return (
        <div className="container" style={{ marginTop: 40 }}>
            <Homepage />
            <div className="row mt-4">
                <div className="col-md-6">
                    <Login onLogin={onLogin} />
                </div>
                <div className="col-md-6">
                    <Register onComplete={onRegisterComplete} />
                </div>
            </div>
        </div>
    );
}

function App() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate ? useNavigate() : null;

    const handleLogin = (userData) => {
        setUser(userData);
        if (navigate) navigate('/profile');
        else window.location.href = '/profile';
    };
    const handleRegisterComplete = () => {
        if (navigate) navigate('/login');
        else window.location.href = '/login';
    };

    return (
        <>
            <SpaceBackground />
            <Navbar user={user} setUser={setUser} />
            <Routes>
                <Route path="/" element={<HomeWithAuth user={user} setUser={setUser} onLogin={handleLogin} onRegisterComplete={handleRegisterComplete} />} />
                <Route path="/register" element={<Register onComplete={handleRegisterComplete} />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/profile" element={user ? <Profile user={user} /> : <Navigate to="/login" />} />
                <Route path="/posts" element={user ? <Posts user={user} /> : <Navigate to="/login" />} />
                <Route path="/messages" element={user ? <Messages user={user} /> : <Navigate to="/login" />} />
            </Routes>
        </>
    );
}

export default function AppWithRouter() {
    return (
        <Router>
            <App />
        </Router>
    );
}
